function showHide(b){var a=document.getElementById(b);a.style.display=(a.style.display=="")?("none"):("")}function alternaTipoConsulta(a){for(contador=0;contador<a.length;contador++){document.getElementById(a.options[contador].value).style.display="none"}document.getElementById(a.options[a.selectedIndex].value).style.display=""}function setTribunal(a,c,d){var b=document.getElementById(d);document.getElementById(a).value=b.value;document.getElementById(c).value=b.options[b.selectedIndex].text};